def is_prime(n):
    if n <= 1 or (n % 2 == 0 and n > 2): 
        return False
    return all(n % i for i in range(3, int(n**0.5) + 1, 2))

def is_digit_sum_prime(num):
    return is_prime(sum(int(digit) for digit in str(num)))

a = int(input())
print("YES" if is_digit_sum_prime(a) else "NO")

