def is_prime(n):
    if n == 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    i = 3
    while i * i <= n:
        if n % i == 0:
            return False
        i += 2
    return True

def counterexample(n):
    m = 1
    while True:
        if is_prime(n * m + 1):
            return m
        m += 1

