def solve():
    n = int(input())
    a = list(map(int, input().split()))
    total = sum(a)
    if total % 2 == 1:
        return "NO"
    dp = [0] * (total // 2 + 1)
    dp[0] = 1
    for x in a:
        for i in range(total // 2, x - 1, -1):
            dp[i] = dp[i] or dp[i - x]
    return "YES" if dp[total // 2] else "NO"

def is_possible(n, boxes):
    total = sum(boxes)
    if total % 2 != 0:
        return "NO"
    half = total // 2
    dp = [0] * (half + 1)
    dp[0] = 1
    for i in range(n):
        for j in range(half, boxes[i] - 1, -1):
            dp[j] = max(dp[j], dp[j - boxes[i]])
    return "YES" if dp[half] else "NO"

