MOD = 1000000007

def solve(n, k, a):
    a.sort(reverse=True)
    dp = [0] * (k + 1)
    dp[0] = 1
    for x in a:
        for i in range(k, x - 1, -1):
            dp[i] = (dp[i] + dp[i - 1]) % MOD
    return sum(dp) % MOD

