import sys

def stopwatch():
    N = int(sys.stdin.readline())
    times = [int(sys.stdin.readline()) for _ in range(N)]
    total_time = 0
    for i in range(N):
        if i % 2 == 0:
            total_time += times[i]
        else:
            total_time += times[i] - times[i-1]
    if total_time % 2 == 1:
        print("still running")
    else:
        print(total_time)

