def adjust_time(direction, minutes, hours, minutes_passed):
    if direction == "F":
        minutes_passed += minutes
    else:
        minutes_passed -= minutes

    hours += minutes_passed // 60
    minutes_passed %= 60

    hours %= 24

    return hours

def time_adjustment(direction, D, H, M):
    if direction == 'F':
        M += D
        while M >= 60:
            M -= 60
            H += 1
        while H >= 24:
            H -= 24
        return H, M
    else:
        M
